section.literal_block:
/* description: Parses simple arithmetic expressions */
// lexical section of the grammar // ==============================
%lex %% \s+ /* no return statement, so skip whitespace */ [A-Z] return "PRI" /* A primary is an individual upper-case letter */ "*" return "TIMES" "/" return "DIV" "-" return "MINUS" "+" return "PLUS" "(" return "LPAREN" ")" return "RPAREN" <<EOF>> return "EOF" . return "INVALID"
/lex
%start program
// phrase-structure section of the grammar // =======================================
%%
program : exp "EOF" { return "The program is valid"; } /* returned by a successful parse */ ; exp : trm | exp "PLUS" trm | exp "MINUS" trm ; trm : fac | trm "TIMES" fac | trm "DIV" fac ; fac : "PRI" | "LPAREN" exp "RPAREN" ;
